    if (navlinkid?.state != null) {
      await axiosBaseurl.post('teamlistdevs', { 'bid': boardDetails[0].board_id }).then((result) => {
        setgroupdetails(result.data);
        // setgroupMembers(groupmem.state.batchdetails);
      }).catch((error) => {
        console.log(error)
      });
    } else {
